mapper.select:
select lpp.id,lpp.order_no,ltm.city_name,ltm.product_type,ltm.product_name,ltm.borrower, ltm.amount,ltm.rate,ltm.rate_unit,lpp.repayment_method,lpp.trans_main_id, lpp.period,ltm.period_total,ltm.paid_period, lpp.repayment_total-lpp.paid_capital-lpp.paid_interest repayment_total, lpp.repayment_capital-lpp.paid_capital repayment_capital,lpp.paid_capital, lpp.repayment_capital_time,lpp.repayment_interest-lpp.paid_interest repayment_interest, lpp.paid_interest,lpp.repayment_interest_time, lpp.overdue_days,lpp.overdue_interest_total,ltm.lending_time, luc.company_name from loan_pay_plan lpp inner join loan_trans_main ltm on lpp.trans_main_id = ltm.trans_main_id left join bump_union_company luc on ltm.company_id = luc.company_id where 1=1
order by lpp.repayment_interest_time,lpp.repayment_capital_time
limit #{page.pageStartIndex},#{page.pageSize}

  select 

  from loan_pay_plan 
	where 1=1

		and id = #{id}


		and trans_main_id = #{transMainId}



      and period=#{period}


		and status = #{status}

	order by trans_main_id desc,period asc

  select sum(repayment_capital+repayment_interest) repayment_total,sum(repayment_capital) repayment_capital,sum(repayment_interest) repayment_interest
  from loan_pay_plan where 
  trans_main_id=#{transMainId} 

		and period=#{period} 


		and account_id = #{accountId}

	and status in('03','90')

select sum(repayment_total) from ( select sum(lpp.repayment_capital)-sum(lpp.paid_capital) as repayment_total, luc.company_name,ltm.department,ltm.account_manager,ltm.provience_name,ltm.city_name,lpp.repayment_capital_time from loan_pay_plan lpp inner join loan_trans_main ltm on lpp.trans_main_id = ltm.trans_main_id left join bump_union_company luc on ltm.company_id = luc.company_id LEFT JOIN loan_customer_info lci on lpp.customer_id=lci.customer_id left join bump_dept ld on ltm.field1=ld.dept_id where 1=1
UNION select sum(lpp.repayment_interest)+sum(lpp.overdue_interest_total)-sum(lpp.paid_interest)-sum(lpp.paid_overdue) as repayment_total, luc.company_name,ltm.department,ltm.account_manager,ltm.provience_name,ltm.city_name,lpp.repayment_interest_time as repayment_capital_time from loan_pay_plan lpp inner join loan_trans_main ltm on lpp.trans_main_id = ltm.trans_main_id left join bump_union_company luc on ltm.company_id = luc.company_id LEFT JOIN loan_customer_info lci on lpp.customer_id=lci.customer_id left join bump_dept ld on ltm.field1=ld.dept_id where 1=1
) total

select sum(repayment_total) from ( select lpp.customer_name, sum(lpp.repayment_capital)-sum(lpp.paid_capital) as repayment_total, ltm.field4, luc.company_name,ltm.department,ltm.account_manager,ltm.provience_name,ltm.city_name,lpp.repayment_capital_time,lci.customer_mobile from loan_pay_plan lpp inner join loan_trans_main ltm on lpp.trans_main_id = ltm.trans_main_id left join bump_union_company luc on ltm.company_id = luc.company_id LEFT JOIN loan_customer_info lci on lpp.customer_id=lci.customer_id where 1=1
UNION select lpp.customer_name, sum(lpp.repayment_interest)+sum(lpp.overdue_interest_total)-sum(lpp.paid_interest)-sum(lpp.paid_overdue) as repayment_total, ltm.field4, luc.company_name,ltm.department,ltm.account_manager,ltm.provience_name,ltm.city_name,lpp.repayment_interest_time as repayment_capital_time,lci.customer_mobile from loan_pay_plan lpp inner join loan_trans_main ltm on lpp.trans_main_id = ltm.trans_main_id left join bump_union_company luc on ltm.company_id = luc.company_id LEFT JOIN loan_customer_info lci on lpp.customer_id=lci.customer_id where 1=1
) total
